import { Component } from 'react';
import TodoContainer from './components/TodoContainer';
import TodoItem from './components/TodoItem';
import './styles/Todo.css';

class App extends Component {
  constructor(props) {
    super(props)

    const lcln = localStorage.length;
    for( var i = 0; i<lcln; i+=2 ){
      console.log(localStorage.getItem( localStorage.key(i) ));
      this.setState({
        items: [
          {id: i+1, 
            name: localStorage.getItem( localStorage.key(i) ), 
            desc: localStorage.getItem( localStorage.key(i+1) )
          }
        ]
      })
    }

    this.state = {
        // items: [
        //     {id: 1, name: "Task 1", desc: "this is my task one"},
        //     {id: 2, name: "Task 2", desc: "this is my task two"},
        //     {id: 3, name: "Task 3", desc: "this is my task three"},              
        // ]
        items: []        
    }
  }

  // componentDidMount(){
    // addItem = (item) => {
