  // }

  addItem = (item) => {
    item.id = this.state.items.length + 1;
    let items = this.state.items;
    items.push(item);            
    // localStorage.setItem('myValueInLocalStorage', items);
    this.setState({items});    
    // console.log( localStorage.getItem('LocalName'), localStorage.getItem('LocalDesc') );
    const lcln = localStorage.length;
    for( var i = 0; i<lcln; i++ ){
      console.log(localStorage.getItem( localStorage.key(i) ));
    }
  }

  // componentDidUpdate(nextProps, nextState){
